import React, { useState } from "react";

function App() {
  const backgroundImages = ["https://www.visitsingapore.com/editorials/singapore-most-iconic-landmarks/jcr:content/par/mobile_21_content_sl/sliderccpar1/editorial_generic_co.thumbnail.overview-image.1460.822.jpg"];
  const baseURL =
    "https://tih-api.stb.gov.sg/content/v1/attractions/search?keyword=park&sortBy=name&sortOrder=asc&language=en&apikey=AALYYjcafVG4UE8jddwk7OeqnWAvgluD";

  const fetchAttractions = async (url) => {
    try {
      const res = await fetch(url);
      const attractions = await res.json();
      console.log(attractions.data);
      setImage(attractions.data[4].images[0].url);
    } catch (err) {
      console.log(err.message);
    }
  };

  const retrieveData = () => {
    fetchAttractions(baseURL);
  };

  console.log(window.innerHeight)
  const webpageStyles = {
    backgroundImage: `url(${backgroundImages[0]})`,
    backgroundSize: "cover",
    width: "100%",
    height: `${window.innerHeight}px`
  }
  const [image, setImage] = useState("");
  return (
    <div style = {webpageStyles}>
      <h2>GA SEI-33</h2>
      <button onClick={retrieveData}>All Park Attractions</button>
      <img src={image} />
    </div>
  );
}

export default App;
