import React from "react";
import { useParams } from "react-router";
import styles from "./Results.module.css";
import ReactStars from "react-rating-stars-component";

const Details = (props) => {
  const params = useParams();
  const names = props.results.map((result) => result.name);
  const resultToDisplay = props.results[names.indexOf(params.name)];
  console.log(resultToDisplay);
  const allReviews = resultToDisplay.reviews;
  console.log(allReviews);
  const reviewsDisplay = allReviews.map((review) => {
    return (
      <>
        <p>
          {review.authorName}
          <span style={{ float: "right" }}>{review.time.split("T")[0]}</span>
        </p>
        <div>
          <ReactStars
            value={review.rating}
            size={18}
            isHalf={true}
            edit={true}
            activeColor="rgb(248, 142, 62)"
          />
        </div>
        <p style={{fontSize: "small"}}>{review.text}</p>
      </>
    );
  });

  //console.log(props.results.indexOf())
  return (
    <div className={styles.results} style={{ position: "relative" }}>
      <h3>
        {resultToDisplay.name}{" "}
        <span style={{ float: "right" }}>{resultToDisplay.rating}/5</span>
      </h3>
      <div className={styles.rating}>
        <ReactStars
          value={resultToDisplay.rating}
          size={24}
          isHalf={true}
          edit={false}
          activeColor="#ECCC6F"
        />
      </div>
      <br />
      <p>
        Address:{" "}
        {resultToDisplay.address.streetName !== ""
          ? `${resultToDisplay.address.block} ${resultToDisplay.address.streetName}`
          : "-"}
      </p>
      <p>Nearest MRT station: {resultToDisplay.nearestMrtStation}</p>
      <p>
        Official Website:{" "}
        {resultToDisplay.officialWebsite !== ""
          ? `${resultToDisplay.officialWebsite}`
          : "-"}
      </p>
      <p>Pricing: {resultToDisplay.pricing.others}</p>
      <br />
      <h4>Reviews: </h4>
      <>
        {reviewsDisplay}
        {allReviews.map((review) => {
          return <p>{review.text.slice(0, 100)}...</p>;
        })}
      </>
    </div>
  );
};

export default Details;
