  const chooseBackground = () => {
    return(Math.floor(Math.random() * backgroundImages.length))
  }
  useEffect(() => {
    backgroundImageChosen = chooseBackground();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const webpageStyles = {
    backgroundImage: `url(${
      backgroundImages[backgroundImageChosen]
    })`,
    backgroundSize: "cover",
    width: `${width}px`,
    height: `${height}px`,
  };

  const [image, setImage] = useState("");
  return (
    <div style={webpageStyles}>
      <NavBar />
      <Search onSubmit={retrieveData} />
    </div>
  );
}

export default App;
