// src/DisplayMapClass.js
import { map } from "@here/maps-api-for-javascript";
import * as React from "react";

export class DisplayMapClass extends React.Component {
  mapRef = React.createRef();

  state = {
    // The map instance to use during cleanup
    map: null,
  };

  componentDidMount() {
    const H = window.H;
    const platform = new H.service.Platform({
      apikey: "{HERE-API-KEY}",
    });

    const defaultLayers = platform.createDefaultLayers();

    // Create an instance of the map
    const map = new H.Map(
      this.mapRef.current,
      defaultLayers.vector.normal.map,
      {
        // This map is centered over Singapore
        center: { lat: 1.29, lng: 103.85 },
        zoom: 5,
        // pixelRatio: window.devicePixelRatio || 1,
      }
    );
    this.setState({ map });

    console.log(this.props.plan)
    const svgMarkup = '<svg width="24" height="24" ' +
    'xmlns="http://www.w3.org/2000/svg">' +
    '<rect stroke="white" fill="#1b468d" x="1" y="1" width="22" ' +
    'height="22" /><text x="12" y="18" font-size="12pt" ' +
    'font-family="Arial" font-weight="bold" text-anchor="middle" ' +
    `fill="white">h</text></svg>`;
    const icon = new H.map.Icon(svgMarkup)
    const coords = {lat: 1.29, lng: 103.85}
    const marker = new H.map.Marker(coords, {icon: icon});
    map.addObject(marker)
  }

//   componentWillUnmount() {
//     // Cleanup after the map to avoid memory leaks when this component exits the page
//     this.state.map.dispose();
//   }

  render() {
    return (
      // Set a height on the map so it will display
      <div ref={this.mapRef} style={{margin: "0 auto", width: "809px", height: "500px" }} />
    );
  }
}
