import React, { useState, useEffect } from "react";
import Main from "./components/Main";
import NavBar from "./components/NavBar";
import Search from "./components/Search";
import SearchResults from "./components/SearchResults";


function App() {

  const [results, setResults] = useState([]);

  const fetchAttractions = async (url) => {
    try {
      const res = await fetch(url);
      const attractions = await res.json();
      setResults(attractions.data);
      console.log(results);
    } catch (err) {
      console.log(err.message);
    }
  };

  const retrieveData = (input) => {
    const search = input;
    const baseURL = `https://tih-api.stb.gov.sg/content/v1/attractions/search?keyword=${search}&sortBy=name&sortOrder=asc&language=en&apikey=AALYYjcafVG4UE8jddwk7OeqnWAvgluD`;
    fetchAttractions(baseURL);
    setHasSearched(true)
  };

  const displayData = (foundResults) => {
    if (foundResults.length === 0) {
      alert("Your search did not yield any result.");
    }
  };

  const [hasSearched, setHasSearched] = useState(false)

  return (
    <div>
      <NavBar />
      <Main />
      {hasSearched? <SearchResults results={results} /> : <Search onSubmit={retrieveData} />}
    </div>
  );
}

export default App;
