import React, {useState} from 'react';
import SearchResult from './SearchResult';
import styles from "./Results.module.css"

const SearchResults = (props) => {
    const originalArray = [...props.results];

    const [query, setQuery] = useState("")

    const handleChange = (e) => {
        setQuery(e.target.value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        props.onSubmit(query, nameSort)
        // setQuery("")
    }

    const [nameSort, setNameSort] = useState("asc");
    const handleSelect = (e) => {
        e.preventDefault();
        console.log(e.target.value)
        setNameSort(e.target.value)
    }

    //nameSort === "asc" ? 

    return (
        <div>
            <form onSubmit = {handleSubmit} style={{textAlign:"center", marginTop: "10px"}}>
                <input style = {{width: "200px", margin: "2px"}} placeholder = "keyword" onChange = {handleChange} value={query}></input> 
                <button type="submit" onClick = {handleSubmit}>Search</button>
            </form>
            <div className = {styles.allResults}>
                Results displayed for "{props.user}" 
                <select onChange = {handleSelect} style = {{float:"right"}} name="nameSort">
                    <option value="asc">Ascending</option>
                    <option value="desc">Descending</option>
                </select>
                {props.results.map((result) => {
                    return (<SearchResult result={result}/>)
                }
                )}
            </div>
        </div>
    );
};

export default SearchResults;
