import React, { useState } from "react";

const Plan = (props) => {
  const handleAdd = () => {
    console.log("clicked!");
  };
  const wishlistDisplay = props.wishlist.map((want) => {
    return <li>{want}</li>;
  });
  const wishlistStyles = {
    backgroundColor: "rgba(255, 255, 255, 0.6)",
    marginLeft: "50px",
    marginTop: "50px",
    padding: "10px",
    width: "200px",
  };
  const planStyles = {
    backgroundColor: "rgba(255, 255, 255, 0.6)",
    marginLeft: "50px",
    marginRight: "50px",
    marginTop: "50px",
    padding: "10px",
    width: "300px",
    flexGrow: "2",
  };

  const [plan, setPlan] = useState(1);
  const itinerary = (
        <div style={{ display: "flex" }}>
        <input placeholder="time" type="time" step="15min" required></input>
        <select style={{ marginLeft: "10px", flexGrow: "2" }}>
          <option>Activity 1</option>
        </select>
        <i style={{ padding: "auto" }} class="fa fa-minus-circle"></i>
      </div>
      )


  return (
    <div style={{ display: "flex" }}>
      <div className="wishlist" style={wishlistStyles}>
        <p
          style={{
            margin: "0",
            fontFamily: "Corinthia, cursive",
            fontSize: "xx-large",
          }}
        >
          Your Wishlist
        </p>
        <ul>{wishlistDisplay}</ul>
      </div>
      <div style={planStyles}>
        <p
          style={{
            margin: "0",
            fontFamily: "Corinthia, cursive",
            fontSize: "xx-large",
          }}
        >
          The Plan
        </p>
        {itinerary}
        <i
          className="fa fa-plus-circle"
          style={{ cursor: "pointer", fontSize: "30px", float: "right" }}
          onClick={handleAdd}
        ></i> 
      </div>
    </div>
  );
};

export default Plan;
